mapper.insert:
    update CIP_ORGANIZATION 
	set NAME = #{NAME} 			
		, TELEPHONE = #{TELEPHONE} 			
		, ORDER_NUMBER = #{ORDER_NUMBER} 			
		, PARENT_ORGANIZATION_SEQ = null 			
	where ORGANIZATION_SEQ = #{ORGANIZATION_SEQ}


    INSERT INTO MEMBER (
		ID			
		, PASSWORD			
		, NAME			
		, EMAIL			
		, ADDRESS		
	)
	values(
		#{ID}			
		, #{PASSWORD}			
		, #{NAME}			
		, #{EMAIL}			
		, #{ADDRESS}			
	)
